    const value = components.d455b213-dd22-403f-8d95-8cc79534109a.value;
    const numValue = Number(value);

    // Check if empty
    if (!value || value === '') return 'Credit amount cannot be empty';

    // Check if it's a valid number
    if (isNaN(value) || isNaN(numValue)) return 'Credit amount must be a whole number';

    // Check if it's an integer
    if (!Number.isInteger(numValue)) return 'Credit amount must be a whole number';

    // Check if it's a multiple of 100
    if (numValue % 100 !== 0) return 'Credits can only be purchased in multiples of 100';

    // Check minimum value
    if (numValue < 2000) return 'Minimum 2000 credits must be purchased at a time';

    // All validations passed
    return '';
  })()}}

