function parseVersion(versionStr) {
  // Remove any prefix like 'v' or 'EE-LTS-'
  const cleanVersion = versionStr.replace(/^(v|EE-LTS-)/, '');

  // Split into numeric components and convert to numbers
  return cleanVersion.split('.').map(Number);
}

function compareVersions(v1, v2) {
  const parsedV1 = parseVersion(v1);
  const parsedV2 = parseVersion(v2);

  // Compare each numeric component
  for (let i = 0; i < Math.max(parsedV1.length, parsedV2.length); i++) {
      const num1 = parsedV1[i] || 0;
      const num2 = parsedV2[i] || 0;

      if (num1 > num2) return 1;
      if (num1 < num2) return -1;
  }

  return 0;
}

function getVersionsBetween(allVersions, fromVersion, toVersion) {
  // Check if select order is opposite
  if (compareVersions(fromVersion, toVersion) > 0) {
    actions.showAlert('error', "Invalid Selection Order");
    return [];
  }

  // Extract tags from the objects, make unique, and sort
  const uniqueVersions = [...new Set(
      allVersions.map(version => version.tag)
  )];
  const sortedVersions = uniqueVersions.sort(compareVersions);

  // Filter versions between fromVersion and toVersion (inclusive)
  return sortedVersions.filter(version => 
      compareVersions(version, fromVersion) >= 0 && 
      compareVersions(version, toVersion) <= 0
  );
}

// Call the function at the end
const versionsBetween = getVersionsBetween(
  queries.fetchFilters.data.filter(note => components.includeBetaVersionsToggle.value || note.is_lts), 
  components.selfHostedVersionFrom.value, 
  components.selfHostedVersionTo.value
);
return versionsBetween

