function parseVersion(versionStr) {
  if (!versionStr || typeof versionStr !== 'string') return [];

  // Remove leading 'v' and trailing suffix like '-ee-lts' or '-cloud-lts'
  const cleanVersion = versionStr
    .trim()
    .toLowerCase()
    .replace(/^v/, '')                 // remove leading 'v'
    .replace(/-(ee|cloud)-lts$/, ''); // remove trailing '-ee-lts' or '-cloud-lts'

  return cleanVersion.split('.').map(num => Number(num) || 0);
}

function compareVersions(v1, v2) {
  const parsedV1 = parseVersion(v1);
  const parsedV2 = parseVersion(v2);

  for (let i = 0; i < Math.max(parsedV1.length, parsedV2.length); i++) {
    const num1 = parsedV1[i] || 0;
    const num2 = parsedV2[i] || 0;

    if (num1 > num2) return 1;
    if (num1 < num2) return -1;
  }

  return 0;
}

function getVersionsBetween(allVersions, fromVersion, toVersion) {
  if (!fromVersion || !toVersion) {
    return [];
  }

  if (compareVersions(fromVersion, toVersion) > 0) {
    actions.showAlert('error', "Invalid Selection Order");
    return [];
  }

  const uniqueVersions = [...new Set(
    allVersions
      .map(version => version.tag)
      .filter(tag => !!tag)
  )];

  return uniqueVersions.filter(version => 
    compareVersions(version, fromVersion) >= 0 &&
    compareVersions(version, toVersion) <= 0
  );
}

