async function connectSSEWithJSON(payload) {
  const response = await fetch(components.api_url.value, {
    method: "POST",
    headers: {
    "Content-Type": "application/json",
    "Accept": "text/event-stream"
    },
    body: JSON.stringify(payload),
  });

  if (!response.ok) {
    throw new Error(`SSE connection failed: ${response.status} ${response.statusText}`);
  }

  const reader = response.body.getReader();
  const decoder = new TextDecoder("utf-8");
  let buffer = "";

  while (true) {
    const { done, value } = await reader.read();
    if (done) {
      console.log("SSE connection closed");
      break;
    }

    buffer += decoder.decode(value, { stream: true });

    // SSE messages are separated by double newlines
    let parts = buffer.split("\n\n");
    buffer = parts.pop(); // keep unfinished chunk

    for (const part of parts) {
      const lines = part.split("\n");
      let data = "";

      for (const line of lines) {
        if (line.startsWith("data:")) {
          data += line.replace(/^data:\s?/, "");
        }
      }

      if (data) {
        try {
          const parsedData = JSON.parse(data)
          console.log("SSE message:", parsedData);
          if(parsedData.result){
            actions.setPageVariable('appJson', parsedData.result)
          }
        } catch {
          console.log("SSE message:", data);
        }
      }
    }
  }
}

