// Helper function to generate progress bar HTML
function generateProgressBarHTML(progressData) {
  const { step = 0, totalSteps = 5, message = 'Processing...', completed = false } = progressData;
  const percentage = (step / totalSteps) * 100;

  // Determine status color
  let statusColor = '#3b82f6'; // blue
  if (completed && step === totalSteps) {
    statusColor = '#22c55e'; // green
  }

  // Step details
  const stepNames = [
    'Creating component tree',
    'Processing layout',
    'Processing events',
    'Processing queries',
    'Generating app JSON'
  ];

  const currentStepName = stepNames[step - 1] || 'Initializing';

  // Build step indicators
  let stepsHTML = '';
  for (let i = 1; i <= totalSteps; i++) {
    const isActive = i === step;
    const isCompleted = i < step;
    const isCurrent = i === step;

    let stepClass = 'step-pending';
    let stepIcon = '‚óã';

    if (isCompleted) {
      stepClass = 'step-completed';
      stepIcon = '‚úì';
    } else if (isCurrent) {
      stepClass = 'step-active';
      stepIcon = '‚óâ';
    }

    stepsHTML += `
      <div class="step ${stepClass}">
        <div class="step-icon">${stepIcon}</div>
        <div class="step-label">${stepNames[i - 1]}</div>
      </div>
    `;
  }

  return `
    <div style="width: 100%; padding: 20px; font-family: system-ui, -apple-system, sans-serif;">
      <!-- Progress Bar -->
      <div style="margin-bottom: 20px;">
        <div style="display: flex; justify-content: space-between; margin-bottom: 8px;">
          <span style="font-size: 14px; font-weight: 600; color: #374151;">
            ${currentStepName}
          </span>
          <span style="font-size: 14px; font-weight: 600; color: #6b7280;">
            ${step}/${totalSteps} (${Math.round(percentage)}%)
          </span>
        </div>
        <div style="width: 100%; height: 12px; background-color: #e5e7eb; border-radius: 9999px; overflow: hidden;">
          <div style="
            width: ${percentage}%; 
            height: 100%; 
            background-color: ${statusColor}; 
            transition: width 0.3s ease;
            border-radius: 9999px;
          "></div>
        </div>
      </div>

      <!-- Current Message -->
      <div style="
        padding: 12px 16px; 
        background-color: #f3f4f6; 
        border-left: 4px solid ${statusColor}; 
        border-radius: 4px;
        margin-bottom: 20px;
      ">
        <p style="margin: 0; font-size: 13px; color: #374151;">
          ${message}
        </p>
      </div>

      <!-- Step Indicators -->
      <div style="display: grid; gap: 8px;">
        ${stepsHTML}
      </div>

      <style>
        .step {
          display: flex;
          align-items: center;
          gap: 12px;
          padding: 8px 12px;
          border-radius: 6px;
          transition: all 0.2s ease;
        }

        .step-pending {
          background-color: #f9fafb;
          color: #9ca3af;
        }

        .step-active {
          background-color: #dbeafe;
          color: #3b82f6;
          font-weight: 500;
        }

        .step-completed {
          background-color: #d1fae5;
          color: #059669;
        }

        .step-icon {
          font-size: 18px;
          width: 24px;
          height: 24px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-weight: bold;
        }

        .step-label {
          font-size: 13px;
        }
      </style>
    </div>
  `;
}

async function connectSSEWithJSON(payload) {
  // Initialize progress bar
  components.progressBar.setText(generateProgressBarHTML({
    step: 0,
    totalSteps: 5,
    message: 'Connecting to server...'
  }));

  const response = await fetch(components.api_url.value, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      "Accept": "text/event-stream"
    },
    body: JSON.stringify(payload),
  });

  if (!response.ok) {
    const errorHTML = `
      <div style="padding: 20px; text-align: center;">
        <div style="color: #dc2626; font-size: 16px; font-weight: 600; margin-bottom: 8px;">
          ‚ùå Connection Failed
        </div>
        <div style="color: #6b7280; font-size: 14px;">
          ${response.status} ${response.statusText}
        </div>
      </div>
    `;
    components.progressBar.setText(errorHTML);
    throw new Error(`SSE connection failed: ${response.status} ${response.statusText}`);
  }

  const reader = response.body.getReader();
  const decoder = new TextDecoder("utf-8");
  let buffer = "";

  while (true) {
    const { done, value } = await reader.read();
    if (done) {
      console.log("SSE connection closed");
      break;
    }

    buffer += decoder.decode(value, { stream: true });

    // SSE messages are separated by double newlines
    let parts = buffer.split("\n\n");
    buffer = parts.pop(); // keep unfinished chunk

    for (const part of parts) {
      const lines = part.split("\n");
      let eventType = "message";
      let data = "";

      for (const line of lines) {
        if (line.startsWith("event:")) {
          eventType = line.replace(/^event:\s?/, "").trim();
        } else if (line.startsWith("data:")) {
          data += line.replace(/^data:\s?/, "");
        }
      }

      if (data) {
        try {
          const parsedData = JSON.parse(data);

          switch (eventType) {
            case "progress":
              console.log("Progress update:", parsedData);

              // Update progress bar with detailed information
              let detailedMessage = parsedData.message;

              // Add more context based on available data
              if (parsedData.pageName) {
                detailedMessage += ` (${parsedData.pageName})`;
              }
              if (parsedData.currentDepth !== undefined && parsedData.maxDepth !== undefined) {
                detailedMessage += ` - Depth ${parsedData.currentDepth}/${parsedData.maxDepth}`;
              }
              if (parsedData.componentCount) {
                detailedMessage += ` - ${parsedData.componentCount} components`;
              }
              if (parsedData.totalBatches) {
                detailedMessage += ` - ${parsedData.successfulBatches}/${parsedData.totalBatches} batches completed`;
              }

              components.progressBar.setText(generateProgressBarHTML({
                step: parsedData.step || 0,
                totalSteps: parsedData.totalSteps || 5,
                message: detailedMessage,
                completed: parsedData.completed || false
              }));

              // Store progress data in page variable for debugging
              actions.setPageVariable('sseProgress', parsedData);
              break;

            case "complete":
              console.log("SSE complete:", parsedData);

              // Show completion progress bar
              components.progressBar.setText(generateProgressBarHTML({
                step: 5,
                totalSteps: 5,
                message: '‚úÖ Migration completed successfully!',
                completed: true
              }));

              // Store result and show modal
              actions.setPageVariable('appJson', parsedData.result);

              // Wait a moment to show the completion state
              setTimeout(() => {
                actions.showModal('confirmation_modal');
              }, 1000);
              break;

            case "error":
              console.error("SSE error:", parsedData);

              // Show error state
              const errorHTML = `
                <div style="padding: 20px; text-align: center;">
                  <div style="color: #dc2626; font-size: 16px; font-weight: 600; margin-bottom: 8px;">
                    ‚ùå Error Occurred
                  </div>
                  <div style="color: #6b7280; font-size: 14px;">
                    ${parsedData.message || 'An unexpected error occurred'}
                  </div>
                </div>
              `;
              components.progressBar.setText(errorHTML);

              actions.showAlert('error', parsedData.message || 'An error occurred');
              break;

            case "agent-chunk":
            case "events-agent-chunk":
            case "queries-agent-chunk":
              // Optional: Log agent streaming output without updating progress bar
              console.log(`${eventType}:`, parsedData);
              break;

            default:
              console.log("SSE message:", parsedData);
              if (parsedData.result) {
                actions.setPageVariable('appJson', parsedData.result);
              }
              break;
          }

        } catch (err) {
          console.log("Non-JSON SSE message:", data);
        }
      }
    }
  }
}

